import React, { useState } from 'react';
import axios from 'axios';
import './App.css';

export default function EmployeeForm() {
    const [formData, setFormData] = useState({
        EmployeeName: '',
        EmployeeID: '',
        EmployeeEmail: '',
        PhoneNumber: '',
        Department: '',
        DateOfJoining: '',
        Role: '',
    });

    const [errors, setErrors] = useState({});
    const [feedbackMessage, setFeedbackMessage] = useState('');

    const validate = () => {
        const newErrors = {};
        if (!formData.EmployeeName) newErrors.EmployeeName = "Employee Name is required";
        if (!formData.EmployeeID) newErrors.EmployeeID = "Employee ID is required";
        if (!formData.EmployeeEmail) newErrors.EmployeeEmail = "Valid Email is required";
        if (!formData.PhoneNumber) {
            newErrors.PhoneNumber = "Phone Number is required";
        } else if (!/^\d{10}$/.test(formData.PhoneNumber)) {
            newErrors.PhoneNumber = "Phone Number must be exactly 10 digits";
        }
        if (!formData.Department) newErrors.Department = "Department is required";
        if (!formData.DateOfJoining) newErrors.DateOfJoining = "Date of Joining is required";
        if (!formData.Role) newErrors.Role = "Role is required";
        setErrors(newErrors);
        return Object.keys(newErrors).length === 0;
    };

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({ ...formData, [name]: value });
        setErrors({ ...errors, [name]: '' });
    };

    const handleReset = () => {
        setFormData({
            EmployeeName: '',
            EmployeeID: '',
            EmployeeEmail: '',
            PhoneNumber: '',
            Department: '',
            DateOfJoining: '',
            Role: '',
        });
        setErrors({});
        setFeedbackMessage('');
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        if (!validate()) return;

        try {
            const response = await axios.post('http://localhost:3001/addEmployee', formData);
            setFeedbackMessage(response.data.message);
            handleReset();
        } catch (error) {
            setFeedbackMessage(error.response?.data?.message || 'Submission failed');
        }
    };

    return (
        <form onSubmit={handleSubmit} className="employee-form">
            <h2>Add New Employee</h2>

            <div className="form-group">
                <label htmlFor="EmployeeName">Employee Name <span>*</span></label>
                <input
                    type="text"
                    id="EmployeeName"
                    name="EmployeeName"
                    value={formData.EmployeeName}
                    onChange={handleChange}
                />
                {errors.EmployeeName && <div className="error">{errors.EmployeeName}</div>}
            </div>

            <div className="form-group">
                <label htmlFor="EmployeeID">Employee ID <span>*</span></label>
                <input
                    type="text"
                    id="EmployeeID"
                    name="EmployeeID"
                    value={formData.EmployeeID}
                    onChange={handleChange}
                />
                {errors.EmployeeID && <div className="error">{errors.EmployeeID}</div>}
            </div>

            <div className="form-group">
                <label htmlFor="EmployeeEmail">Email <span>*</span></label>
                <input
                    type="email"
                    id="EmployeeEmail"
                    name="EmployeeEmail"
                    value={formData.EmployeeEmail}
                    onChange={handleChange}
                />
                {errors.EmployeeEmail && <div className="error">{errors.EmployeeEmail}</div>}
            </div>

            <div className="form-group">
                <label htmlFor="PhoneNumber">Phone Number <span>*</span></label>
                <input
                    type="tel"
                    id="PhoneNumber"
                    name="PhoneNumber"
                    value={formData.PhoneNumber}
                    onChange={handleChange}
                />
                {errors.PhoneNumber && <div className="error">{errors.PhoneNumber}</div>}
            </div>

            <div className="form-group">
                <label htmlFor="Department">Department <span>*</span></label>
                <select
                    id="Department"
                    name="Department"
                    value={formData.Department}
                    onChange={handleChange}
                >
                    <option value="">Select Department</option>
                    <option value="HR">HR</option>
                    <option value="Engineering">Engineering</option>
                    <option value="Marketing">Marketing</option>
                </select>
                {errors.Department && <div className="error">{errors.Department}</div>}
            </div>

            <div className="form-group">
                <label htmlFor="DateOfJoining">Date of Joining <span>*</span></label>
                <input
                    type="date"
                    id="DateOfJoining"
                    name="DateOfJoining"
                    value={formData.DateOfJoining}
                    onChange={handleChange}
                    max={new Date().toISOString().split('T')[0]}
                />
                {errors.DateOfJoining && <div className="error">{errors.DateOfJoining}</div>}
            </div>

            <div className="form-group">
                <label htmlFor="Role">Role <span>*</span></label>
                <input
                    type="text"
                    id="Role"
                    name="Role"
                    value={formData.Role}
                    onChange={handleChange}
                />
                {errors.Role && <div className="error">{errors.Role}</div>}
            </div>

            <div className="button-container">
                <button type="submit">Submit</button>
                <button type="button" onClick={handleReset}>Reset</button>
            </div>

            {feedbackMessage && (
                <div className={`feedback-message ${feedbackMessage.includes('success') ? 'success' : 'error'}`}>
                    {feedbackMessage}
                </div>
            )}
        </form>
    );
}
